CREATE TABLE `accounts` (`id` int(11) unsigned NOT NULL auto_increment,`identity_id` int(11) unsigned NOT NULL,`service_id` int(11) unsigned NOT NULL,`service_type_id` int(11) unsigned NOT NULL,`username` varchar(128) NOT NULL,`account_url` varchar(255) NOT NULL,`feed_url` varchar(255) NOT NULL,`created` datetime NOT NULL,`modified` datetime NOT NULL,PRIMARY KEY  (`id`),KEY `identity_id` (`identity_id`),KEY `service_id` (`service_id`),KEY `service_type_id` (`service_type_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
CREATE TABLE `contacts` (`id` int(11) unsigned NOT NULL auto_increment,`identity_id` int(11) unsigned NOT NULL,`with_identity_id` int(11) unsigned NOT NULL,`created` datetime NOT NULL,`modified` datetime NOT NULL,PRIMARY KEY  (`id`),KEY `to_identity_id` (`with_identity_id`),KEY `from_identity_id` (`identity_id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
CREATE TABLE `identities` (`id` int(11) unsigned NOT NULL auto_increment,`username` varchar(128) NOT NULL,`password` varchar(128) NOT NULL,`email` varchar(128) NOT NULL,`firstname` varchar(64) NOT NULL,`lastname` varchar(64) NOT NULL,`hash` varchar(32) NOT NULL,`last_sync` datetime NOT NULL,`created` datetime NOT NULL,`modified` datetime NOT NULL,PRIMARY KEY  (`id`),UNIQUE KEY `username` (`username`),KEY `last_sync` (`last_sync`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
CREATE TABLE `services` (`id` int(11) unsigned NOT NULL auto_increment,`name` varchar(64) NOT NULL,`url` varchar(255) NOT NULL,`service_type_id` int(11) unsigned NOT NULL,`created` datetime NOT NULL,`modified` datetime NOT NULL,PRIMARY KEY  (`id`),KEY `service_type_id` (`service_type_id`),KEY `url` (`url`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
CREATE TABLE `service_types` (`id` int(11) unsigned NOT NULL auto_increment,`token` varchar(16) NOT NULL,`name` varchar(32) NOT NULL,`created` datetime NOT NULL,`modified` datetime NOT NULL,PRIMARY KEY  (`id`),KEY `token` (`token`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
