CREATE TABLE `entries` (`id` int(11) unsigned NOT NULL auto_increment,`identity_id` int(11) unsigned NOT NULL,`account_id` int(11) unsigned NOT NULL,`service_type_id` int(11) unsigned NOT NULL,`published_on` datetime NOT NULL,`title` varchar(255) NOT NULL,`url` varchar(255) NOT NULL,`content` text NOT NULL,`restricted` tinyint(1) unsigned NOT NULL COMMENT '0 = public, 1 = restricted',PRIMARY KEY  (`id`),KEY `identity_id` (`identity_id`),KEY `published_on` (`published_on`),KEY `account_id` (`account_id`),KEY `service_type_id` (`service_type_id`),KEY `public` (`restricted`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8;