CREATE TABLE  `omb_request_tokens` ( `id` INT( 11 ) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY , `identity_id` INT( 11 ) NOT NULL, `token_key` VARCHAR( 255 ) NOT NULL, `token_secret` VARCHAR( 255 ) NOT NULL , `authorized` BOOLEAN DEFAULT FALSE, `created` DATETIME NOT NULL , `modified` DATETIME NOT NULL) ENGINE = MYISAM DEFAULT CHARSET=utf8;
CREATE TABLE  `omb_access_tokens` ( `id` INT( 11 ) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY , `identity_id` INT( 11 ) NOT NULL, `token_key` VARCHAR( 255 ) NOT NULL, `token_secret` VARCHAR( 255 ) NOT NULL , `created` DATETIME NOT NULL , `modified` DATETIME NOT NULL) ENGINE = MYISAM DEFAULT CHARSET=utf8;