CREATE TABLE  `request_tokens` ( `id` INT( 11 ) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY , `consumer_id` INT( 11 ) NOT NULL, `token_key` VARCHAR( 255 ) NOT NULL, `token_secret` VARCHAR( 255 ) NOT NULL , `authorized` BOOLEAN DEFAULT FALSE, `created` DATETIME NOT NULL , `modified` DATETIME NOT NULL) ENGINE = MYISAM ;
CREATE TABLE  `access_tokens` ( `id` INT( 11 ) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY , `consumer_id` INT( 11 ) NOT NULL, `token_key` VARCHAR( 255 ) NOT NULL, `token_secret` VARCHAR( 255 ) NOT NULL , `created` DATETIME NOT NULL , `modified` DATETIME NOT NULL) ENGINE = MYISAM ;